
// when we export something ( variables ): 2 kinds of exports

// 1. Named export (any number of named exports)
// 2. Default export (only one default export )


// named export
export function addTwoNumbers(a, b) {
    return a + b;
}

// named export
export const a = 200;


export default {
    name: "aravind",
    age: 23
};
